mapper.select:
SELECT id, name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
FROM member
WHERE id = #{value}

mapper.update:
UPDATE member
SET passwd = #{passwd}, email = #{email}
WHERE id = #{id}

mapper.select:
